            </div>
            <div className="form-group">
                <label for="exampleInputPassword1">Password</label>
                <input type="password" className="form-control"/>
            </div>
           
            <button className="btn btn-primary" type="submit">Register</button>
            </form>
            
            
        </div>
    );
    


}

