import React, {useEffect, useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';

function Register(){
    useEffect(()=>{
        fetchUsersTable();
    }, []);

    //define the states
    const [users, setUsers] = useState([]);
    const [show, setShow] = useState(true);

    //call the api to retrieve the data
    const fetchUsersTable = async () => {
        const data = await fetch(
            'http://localhost:5000/api/users', {
            method: 'get'
            }
        );
        const jsonData = await data.json();
        setUsers(jsonData);
        console.log(users);
        console.log("ran fetch");
    }

    //Make a register form
    return(
        <div>

            <h1>Register for Trading Practice</h1>
            <div id="formContent">
            

            
            <div class="fadeIn first">
            </div>

            <form>
            <input type="text" id="login" class="fadeIn second" name="login" placeholder="login name"/>
            <input type="text" id="password" class="fadeIn third" name="login" placeholder="password"/>
            <input type="submit" className="fadeIn fourth" value="Log In"/>
            </form>

           
            <div id="formFooter">
            <a className="underlineHover" href="#">Forgot Password?</a>
            </div>

            </div>

            
        </div>
    );
    


}

export default Register;
