import React, { useEffect, useState } from 'react';
import {Redirect} from 'react-router-dom';
import Navbar from './Navbar';
import useHttpGet from '../hooks/httpGet';
import useGetUserStocks from '../hooks/getUserStocks';

function Login({props}) {

  //define the states
  //const [users, setUsers] = useState([]);
  const [username, setUsername] = useState([]);
  const [password, setPassword] = useState('1234');
  
  const [stocks] = useGetUserStocks('http://localhost:5000/api/users/', username, [username]);
  console.log(stocks);
  console.log('re rendered');
  

  const [users] = useHttpGet('http://localhost:5000/api/users', []);
  //call the api to retrieve the users
  // const fetchUsersTable = async () => {
  //   const data = await fetch('http://localhost:5000/api/users', {
  //     method: 'get'
  //   });
  //   const jsonData = await data.json();
  //   setUsers(jsonData);
  //   console.log(users);
  //   console.log('ran fetch');
  // };

  
  const Submit = (e) => {
    e.preventDefault();
    setUsername(e.target[0].value);
      setPassword(e.target[1].value);

      return Validate();
    
    
    
    
  };
  
  const Validate = () => {
    console.log("ran validate")
    for (let i = 0; i < users.length; i++) {
      if (
        users[i].username === username &&
        users[i].password === password
      ) {
        console.log(username);
        console.log(password);
        props.login(username, password);
        return <Redirect push to='/dashboard' />;
      }
    }
  }

  return (
    <div>
      <Navbar />
      <h1>Login to trading practice</h1>
      <form onSubmit={Submit}>
        <div className='form-group'>
          <label for='exampleInputEmail1'>User Name</label>
          <input
            type='text'
            className='form-control'
            placeholder='username'
          />
          <small id='emailHelp' className='form-text text-muted'>
            Login Please
          </small>
        </div>
        <div className='form-group'>
          <label for='exampleInputPassword1'>Password</label>
          <input
            type='password'
            className='form-control'
            placeholder='password'
          />
        </div>
        <button className='btn btn-primary' type="submit">
        Login
      </button>
      </form>
      
    </div>
  );
}

export default Login;
