import React, { useEffect, useState } from 'react';

function BuyStock(props){
    const {name, price} = props.buyStock;
    
    const handleChange = (event) => {
        if([event.target.name] == 'username'){
            setUserName(event.target.value);
        }else if([event.target.name] == 'password'){
            setPassword(event.target.value);
        }else if([event.target.name] == 'passwordConfirmation'){
            setPasswordConfirmation(event.target.value);
        }else{
            console.log(event.target.name);
        }
        
      };
    return(
        

        <div className="card text-white bg-info mb-1" style={{maxWidth: "60rem"}}>
            <div className="card-header">{name}</div>
            <div className="card-body">
            <h5 className="card-title">price: {price}</h5>
            <p className="card-text"></p>
           
            <div class="input-group mb-3">
            <input type="number" class="form-control" placeholder="Amount to buy" aria-describedby="basic-addon2"/>
            <div class="input-group-append">
                <button className="btn btn-primary" onClick = {() => props.handleClick(name)}>Buy</button>
            </div>
            </div>

            </div>
        </div>
    );
}

export default BuyStock;
