import React, { useEffect, useState } from 'react';
import Navbar from './Navbar';
import useHttpGet from '../hooks/httpGet';

function Login() {

  //define the states
  //const [users, setUsers] = useState([]);
  const [username, setUsername] = useState([]);
  const [password, setPassword] = useState([]);

  const [users] = useHttpGet('http://localhost:5000/api/users', []);
  //call the api to retrieve the users
  // const fetchUsersTable = async () => {
  //   const data = await fetch('http://localhost:5000/api/users', {
  //     method: 'get'
  //   });
  //   const jsonData = await data.json();
  //   setUsers(jsonData);
  //   console.log(users);
  //   console.log('ran fetch');
  // };

  const enteredUserName = e => {
    let name = e.target.value;
    //console.log(name);
    setUsername({ name });
  };
  const enteredPassword = e => {
    let pass = e.target.value;
    //console.log(pass);
    setPassword({ pass });
  };
  const Submit = () => {
    for (let i = 0; i < users.length; i++) {
      if (
        users[i].username === username.name &&
        users[i].password === password.pass
      ) {
      }
    }
  };

  return (
    <div>
      <Navbar />
      <h1>Login to trading practice</h1>
      <form>
        <div className='form-group'>
          <label for='exampleInputEmail1'>User Name</label>
          <input
            onChange={enteredUserName}
            type='text'
            className='form-control'
            placeholder='username'
          />
          <small id='emailHelp' className='form-text text-muted'>
            Login Please
          </small>
        </div>
        <div className='form-group'>
          <label for='exampleInputPassword1'>Password</label>
          <input
            onChange={enteredPassword}
            type='password'
            className='form-control'
            placeholder='password'
          />
        </div>
      </form>
      <button className='btn btn-primary' onClick={Submit}>
        Login
      </button>
    </div>
  );
}

export default Login;
