import React, { useState, useEffect } from 'react';
import "../styles/stocksStyle.css";
import BuyStock from './BuyStock';

function BuyStocks(props) {

 const createMessage = (stock) => {
    props.boughtMessage.forEach(message => {
      if(message.name == stock.name){
        return message.message
      }
    })
 }

  return (
    <div className='container'>
      {props.buyStocks.map(stock => (
        
        <div className='stockContainer' key={stock.name}>
            {console.log(stock)}
            <BuyStock buyStock = {stock} handleClick = {props.handleClick} boughtMessage = {() => createMessage(stock)}/>
          
        </div>
      ))}
    </div>
  );
}

export default BuyStocks;
