import React, {useState, useEffect} from 'react';
import './App.css';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import LoginHelp from './components/LoginHelp';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import RegisterHelp from './components/RegisterHelp';
import useHttpGet from './hooks/httpGet';
//use react-router-dom to change urls
function App() {


  useEffect(()=>{
    checkAlreadyLoggedin();
  }, []);

  const [loggedInStatus, setLoggedInStatus] = useState("NOT Logged in");
  const [username, setUsername] = useState("");
  const [money, setMoney] = useState("");
  const [user, setUser] = useState([]);
  const checkAlreadyLoggedin = async () => {

      const data = await fetch(
          "http://localhost:5000/api/user/loggedin", {
          method: 'get'
          }
      );
      const jsonData = await data.json();
      setUser(jsonData);
      console.log(fetchedData);
      console.log("ran fetch");
  }



  const handleLogin = (data) => {
    console.log("data in App.js ran handleLogin", data.username);
    setUsername(data.username);
    setMoney(data.dollars);
    setLoggedInStatus("Logged in");


  }
    return (
    <div>

    <Router>
      <div>
        <Switch>
        <Route path="/" exact render = {props=>(
            <RegisterHelp {...props} handleLogin = {handleLogin} loggedIn = {loggedInStatus}/>
          )}/>
          <Route path="/dashboard" exact render = {props => (
            <Dashboard {...props} loggedIn = {loggedInStatus} username={username} money={money}/>
          )}/>
          <Route path="/login" exact render = {props=>(
            <LoginHelp {...props} handleLogin = {handleLogin} loggedIn = {loggedInStatus}/>
          )}/>


        </Switch>
      </div>
    </Router>
    </div>

  );



}

export default App;
