  }

  return(
    <div>
      <Navbar user = {props.username} handleLogout = {handleLogout}/>
      <h3>Money: {user.dollars}</h3>
      <div className="container">
        
        <UserStocks userStock = {stocks} handleClick = {handleSell}/>
      </div>
    </div>

  );
}

export default Dashboard;
