  const [stocks] = useHttpGet(
    `http://localhost:5000/api/stocks`,
    [],
    'getting stocks'
  );

  const handleLogout = () => {
    sessionStorage.removeItem('username');
    props.handleLogout();
    props.history.push('/');
  };

  const handleBuy = async (name) => {
    console.log("ran handle buy");
