import React, { useEffect, useState} from 'react';
import {Link} from 'react-router-dom';

function Dashboard() {
  useEffect(() => {
    fetchItems();
  }, []);
//define the states
  const [items, setItems] = useState([]);
//call the api to retrieve the data
  const fetchItems = async () => {
    //I retrieve the list of pokemon
    const data = await fetch(
      `http://localhost:5000/api/stocks`, {
          method: 'get'
      }
    );
    const item = await data.json();
    //put the data into the states
    setItems(item);
    console.log(items);
    console.log("ran fetch");
  };

  const imgStyle = {
      height: '300px',
      width: '300px'
  }
  return(
    <div>
      <ul className="navbar">
        <li><a href="/"></a></li>
        <li><a href="/register"></a></li>
        <li><a href="/login"></a></li>
      </ul>
      <h1>Dashboard</h1>
     
      
      <div>
        {/*map through the pokemon and display them on screen a certain way.
        Link is there to allow me to click on a name and bring it to more detail. */}
      {items.map(item => (
        <p key={item.name}>
          {item.name}
          
        </p>
      ))}
      </div>
    </div>

  );
}

export default Dashboard;
