import React, {useEffect, useState} from 'react';



function Register(){
    let warning = "Hello There";
    useEffect(()=>{
        fetchUsersTable();
    }, []);

    //define the states
    const [users, setUsers] = useState([]);
    const [userName, setUserName] = useState([]);
    const [password, setPassword] = useState([]);

    //call the api to retrieve the data
    const fetchUsersTable = async () => {
        const data = await fetch(
            'http://localhost:5000/api/users', {
            method: 'get'
            }
        );
        const jsonData = await data.json();
        setUsers(jsonData);
        console.log(users);
        console.log("ran fetch");
    }

    const Submit = async () => {
        console.log(userName.name);
        console.log(password.pass);
        for(let i = 0; i<users.length; i++){
            if(users[i].userName == userName.name){
                warning = "must use a unique name";
                return;
            }
        }
        const newUser = {
            username: userName.name,
            password: password.pass,
            money: 10000,
            premium_user: false
        }
        console.log(users);
        console.log(newUser);
        const data = await fetch(
            'http://localhost:5000/api/users', {
                method: 'get',
                
                
            }
            
        )
        console.log(await data.json());
        console.log('ran post');
    }

    const enteredUserName = (e) => {
       let name = e.target.value;
       //console.log(name);
       setUserName({name});
    }
    const enteredPassword = (e) => {
        let pass = e.target.value;
        //console.log(pass);
        setPassword({pass});
    }

    //Make a register form
    return(
        <div>

            <h1>Register for Trading Practice</h1>
            <form>
            <div className="form-group">
                <label for="exampleInputEmail1">User Name</label>
                <input onChange={enteredUserName} type="text" className="form-control"/>
                <small id="emailHelp" className="form-text text-muted">{warning}</small>
            </div>
            <div className="form-group">
                <label for="exampleInputPassword1">Password</label>
                <input onChange={enteredPassword} type="password" className="form-control"/>
            </div>
           
            
            </form>
            <button className="btn btn-primary" onClick={Submit}>Submit</button>
            
        </div>
    );
    


}

export default Register;
