import React, { useEffect, useState } from 'react';
import Navbar from './Navbar';
import useHttpGet from '../hooks/httpGet';
import BuyStocks from './BuyStocks';

function Buy(props) {
  const [stocks] = useHttpGet(
    `http://localhost:5000/api/stocks`,
    [],
    'getting stocks'
  );

  const handleLogout = () => {
    sessionStorage.removeItem('username');
    props.handleLogout();
    props.history.push('/');
  };

  const handleBuy = async (name, amountToBuy) => {
    console.log("ran handle buy");
    console.log(amountToBuy);
    const updateThis = {
      username: sessionStorage.getItem('username'),
      name: name,
      amount: amountToBuy
    };
    
    
    const myResponse = await fetch(
        `http://localhost/api/owns`, {
            method: 'Put',
            mode: 'cors', // no-cors, *cors, same-origin
            cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            credentials: 'omit', // include, *same-origin, omit
            headers: {"content-type":"application/json"},
            redirect: 'follow', // manual, *follow, error
            referrerPolicy: 'no-referrer', // no-referrer, *client
            body: JSON.stringify(updateThis)
        });
        const jsonData = await myResponse.json();
        
    
  }
 



  if (!props.username) {
    return <div></div>;
  } else {
    return (
      <div>
        <Navbar user={props.username} handleLogout={handleLogout} />
        <h3>Money: {props.user.dollars}</h3>
        <div className='container'>
          <BuyStocks buyStocks = {stocks} handleClick = {handleBuy}/>
        </div>
      </div>
    );
  }
}

export default Buy;
