import React, {useState, useEffect} from 'react';
import './App.css';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import LoginHelp from './components/LoginHelp';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import RegisterHelp from './components/RegisterHelp';
//use react-router-dom to change urls
function App() {
  const [loggedInStatus, setLoggedInStatus] = useState("NOT Logged in");
  const [user, setUser] = useState([]);

  const handleLogin = (data) => {
    console.log("data in App.js",data);
    setLoggedInStatus("Logged in");
    setUser(data);

  }
    return (
    <div>

    <Router>
      <div>
        <Switch>
        <Route path="/" exact render = {props=>(
            <RegisterHelp {...props} handleLogin = {handleLogin} loggedIn = {loggedInStatus}/>
          )}/>
          <Route path="/dashboard" exact render = {props => (
            <Dashboard {...props} loggedIn = {loggedInStatus} user={user}/>
          )}/>
          <Route path="/login" exact render = {props=>(
            <LoginHelp {...props} handleLogin = {handleLogin} loggedIn = {loggedInStatus}/>
          )}/>


        </Switch>
      </div>
    </Router>
    </div>

  );



}

export default App;
